    select(contains("numeric")) %>% 
    select(-numeric.hist) %>% 
    pivot_longer(cols = everything(), names_to = "통계량", values_to = "값") %>% 
    mutate(통계량 = str_remove(통계량, "numeric."))

수달_stat %>% 
    gt::gt()



